$(function () {
	var style = document.createElement("style");
	style.appendChild(document.createTextNode(""));
	document.head.appendChild(style);
	function addCSSRule(sheet, selector, rules, index) {
		if(sheet.insertRule) {
			style.sheet.insertRule(selector + "{" + rules + "}", index);
		}
		else if(sheet.addRule) {
			style.sheet.addRule(selector, rules, index);
		}
	}
	addCSSRule(document.styleSheets[0], ".fotorama .fotorama__caption", "display: none;");

	var fotorama = $('.fotorama[data-auto="false"]');
	if (fotorama.length > 0) {
		fotorama.after('<div class="fotorama-bottom_caption smaller darkgrey builder-section builder-section--with_indent0"></div><p></p>');
		fotorama.on('fotorama:load ' + 'fotorama:show ',
			function (e, fotorama) {
				zzz = fotorama.activeFrame;
				var caption = fotorama.activeFrame.caption || '';
				var alt = fotorama.activeFrame.alt;
				var description = fotorama.activeFrame.description || '';
				var copyright = fotorama.activeFrame.copyright || '';
				var activeStage = fotorama.activeFrame.$stageFrame;
				if (fotorama.activeFrame.copyright) {
					if (!activeStage.children().hasClass('fotorama__caption')) {
						activeStage.append('<div class="fotorama__caption"><div class="fotorama__caption__wrap"></div></div>');
					}
					activeStage.children('.fotorama__caption').children('.fotorama__caption__wrap').html((description || caption) + '<div class="fotorama__copyright i grey">' + copyright + '</div>');
				}
				fotorama.$caption = fotorama.$caption || $(this).next('.fotorama-bottom_caption');
				fotorama.$caption.html(fotorama.activeFrame.description || fotorama.activeFrame.caption || '');
				if (copyright) {
					fotorama.$caption.append('<div class="fotorama__copyright i grey">' + copyright + '</div>');
				}
				fotorama.$caption.append('<hr style="margin-top: .8em">');
				if ( alt ) {
						activeStage.find('img').first().attr('alt', alt);
						activeStage.find('img').first().attr('title', alt);
				}
				activeStage.children('.fotorama__caption').addClass('fotorama__select');
			})
		.fotorama();	// Initialize fotorama manually
		fotorama.find('.fotorama__caption').hide();
	}

	$('.fotorama a').click(function() {
		location.href = $(this).attr('href');
	});

});

